import React, { useEffect, useState } from 'react';
import './App.css';
import axios from 'axios';
import Description from './Description';

function App() {
  const [ repository, setRepository] = useState<any>(['']);
  const [input, setInput] = useState<string>( )
  const logo: string = 'https://tibiawiki.com.br/images/5/52/Tibia_Logo.png'

  useEffect(() => {
    axios.get(`https://api.tibiadata.com/v2/characters/${input}.json`)
    .then((res)=> {
      setRepository(res)
    })
    .catch((err) => console.log(err)
    )
  },[input])

  const handleSubmit = (e: any) => {
    e.preventDefault()
    console.log(repository)

    return <Description data={repository}/>
  }

  return (
      <div className="App">
        <header className="App-header">
          <img src={logo} alt="tibia" />
          <h1>Tibia Character Information</h1>
          <form onSubmit={handleSubmit}>
            <input type="text" placeholder='Enter character name' value={input} onChange={(e) => setInput(e.target.value)}/>
            <button type='submit'>Check</button>
          </form>
          <table>
          </table>
        </header>
      </div>
  );
}

export default App;
